import './App.css';
import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Home() {
  const [data, setData] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:8081")
      .then(result => {
        console.log(result.data); // Ajoutez cette ligne pour voir ce que vous recevez
        setData(result.data.data || []); // Assurez-vous que `data` est un tableau
      })
      .catch(err => console.log(err));
  }, []); // Dépendance vide pour éviter les appels infinis

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">CRUD Table Design</h1>

      <div className="text-end mb-3">
        <button className="btn btn-primary">Add New Item</button>
      </div>

      <table className="table table-striped table-hover table-bordered">
        <thead className="table-dark">
          <tr>
            <th scope="col">#</th>
            <th scope="col">Title</th>
            <th scope="col">Description</th>
            <th scope="col">Actions</th>
          </tr>
        </thead>
        <tbody>
          {Array.isArray(data) && data.length > 0 ? (
            data.map((item, index) => (
              <tr key={index}>
                <th scope="row">{index + 1}</th>
                <td>{item.title}</td>
                <td>{item.body}</td>
                <td>
                  <button className="btn btn-warning me-2">Edit</button>
                  <button className="btn btn-danger">Delete</button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="4">No data available</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}

export default Home;
