import './App.css';
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link } from 'react-router-dom';

function Home() {
  const [data, setData] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:8081")
      .then(result => {
        console.log("API Response:", result.data); // Affichez la réponse de l'API
        setData(result.data || []); // Utilisez directement result.data
      })
      .catch(err => console.log("Error fetching data:", err));
  }, []);

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">CRUD Table Design</h1>

      <div className="text-end mb-3">
        <Link to="/create" className="btn btn-primary">Add New Item</Link>
      </div>

      <table className="table table-striped table-hover table-bordered">
        <thead className="table-dark">
          <tr>
            <th scope="col">#</th>
            <th scope="col">Title</th>
            <th scope="col">Description</th>
            <th scope="col">Actions</th>
          </tr>
        </thead>
        <tbody>
          {Array.isArray(data) && data.length > 0 ? (
            data.map((item, index) => (
              <tr key={item.id}> {/* Utilisation de item.id comme clé unique */}
                <th scope="row"></th>
                <td>{item.title}</td>
                <td>{item.body}</td>
                <td>
                  <Link to="/edititem/${item.id}" className="btn btn-warning me-2">Edit</Link>
                  <button className="btn btn-danger">Delete</button>
                </td>
              </tr>
            ))
          ) : (
              <tr>
                <td colSpan="4" className="text-center">No data available</td>
              </tr>
            )}
        </tbody>
      </table>
    </div>
  );
}

export default Home;
